$(document).ready(() => {
    /* Show the menu dialog when the help button is pressed. */
    $('#help-button').on('click', () => {
	$('#help').toggle();
    });
    /* Dynamically adds forms to a form set. */
    $('#add-more').click((event) => {
	// id_form-TOTAL_FORMS is generated by django for the empty form.
	const formIndex = $('#id_form-TOTAL_FORMS').val();
	// Replaces the palceholder in the empry form with the current index
	$('#formset').append($('#empty-form').html().replace(/__prefix__/g, formIndex));
	$('#id_form-TOTAL_FORMS').val(parseInt(formIndex) + 1);
    });
});



